choose.when:
	WHERE b.writer = m.id 
  		  AND b.show = 'y' 			  
  		  AND #{map.search_option} LIKE #{map.keyword}

choose.otherwise:
WHERE b.writer = m.id 
  AND show = 'y' 
  AND (title LIKE #{map.keyword}
       OR search_content LIKE #{map.keyword}
  	   OR writer LIKE #{map.keyword})

mapper.update:
		UPDATE tbl_board
     	SET show = 'n'
     	WHERE bno =#{param1}

UPDATE tbl_board
SET viewcnt = viewcnt+1
WHERE bno =#{bno}

UPDATE tbl_board
SET Re_step = Re_step+1
WHERE ref =#{ref} AND re_step>#{re_step}

mapper.select:
SELECT fullname FROM tbl_attach
WHERE bno = #{bno}
ORDER BY regdate DESC
